import React from 'react';
import { Link } from 'react-router-dom';


const Item = ({ prod }) => {
    return (
        <article className="card">
            <img src={prod.img} width="100" height="100" alt={prod.title}/>
            <div className="card-info">
                <h2>{prod.title}</h2>
                <h4>${prod.price}.-</h4>
                <h5>{prod.category}</h5>
                <Link to={`/item/${prod.id}`}>
                    <button>Info</button>
                </Link>
            </div>
        </article>
    );
};

export default Item;
